$(function(){var t=$(".js-isotope").isotope({itemSelector:".person",layoutMode:"masonry",getSortData:{name:".person__name",floor:"[data-floor]"},sortAscending:{name:!0,floor:!1},sortBy:"random"});$("#filters li").on("click",function(){var o=$(this).attr("data-filter");t.isotope({filter:o})}),$("#sorts li").on("click",function(){var o=$(this).attr("data-sort");t.isotope({sortBy:o})}),$(".person").on("click",function(){$(".cards").fadeIn()}),$(".card__close").on("click",function(){$(".cards").fadeOut()})}),$(document).ready(function(){var t=$(".gallery").flickity({cellSelector:"img",imagesLoaded:!0,percentPosition:!1}),o=$(".caption"),e=t.data("flickity");t.on("cellSelect",function(){o.text(e.selectedElement.alt)})});