$(function(){var o=$(".js-isotope").isotope({itemSelector:".person",layoutMode:"masonry",getSortData:{name:".person__name",floor:"[data-floor]"},sortAscending:{name:!0,floor:!1},sortBy:"random"});$("#filters li").on("click",function(){var t=$(this).attr("data-filter");o.isotope({filter:t})}),$("#sorts li").on("click",function(){var t=$(this).attr("data-sort");o.isotope({sortBy:t})}),$(".person").on("click",function(){$(".cards").fadeIn()}),$(".card").on("click",function(){$(".cards").fadeOut()})});