$(function(){var t=$("#isotope").isotope({itemSelector:".person",layoutMode:"masonry",getSortData:{name:".person__name",floor:"[data-floor]"},sortAscending:{name:!0,floor:!1},sortBy:"random"});$("#filters li").on("click",function(){var o=$(this).attr("data-filter");t.isotope({filter:o})}),$("#sorts li").on("click",function(){var o=$(this).attr("data-sort");t.isotope({sortBy:o})})}),$(document).ready(function(){$("#carousel").tinycarousel({bullets:!0,buttons:!1,animation:!0,animationTime:1e3,start:1})});